import logo from './img/logo.png';
import './styles.css';
import { useRef, useState } from 'react';

function App() {

  const dropDownRef = useRef(null);
  const [isActive, setIsActive] = useState(false)
  const onClick = () => setIsActive(!isActive)

  console.log(isActive) 


  return (
   <div classname="container">
     <div classname="menu-container">
       <button onClick={onClick} classname="menu-button">
        <span>MENU</span>
        <img width="20px" height="20px" src="https://www.bing.com/images/blob?bcid=stf2.K6jF18Gzph9fD6JJW0Rxhb5.....xc" alt="menu"></img>
       </button>

       <nav 
       ref={dropDownRef}
          classname={`menu ${isActive ? "active" : "inactive"}`}
        >
        <ul>
          <li>
            <a href="#">cadastrar</a>
          </li>
          <li>
            <a href="#">Login</a>
          </li>
          <li>
            <a href="#">Menu</a>
          </li>
          <li>
            <a href="#">Pedidos</a>
          </li>
          <li>
            <a href="#">cardápio</a>
          </li>
        </ul>
       </nav>
     </div>

      <header className="App-header">
        <img src={logo} classname="app-logo" alt="logo" />
         <code>RESTAURANTE EXFOMEADOS</code>

         <a classname="link-cardapio" href="https://" target="_blank" rel="noopener noreferrer">
            CARDÁPIO 
         </a>
      </header>
   </div>

  );
}

export default App;
