import logo from './img/logo.png'
import './styles.css';
/*import React { useRef, useState } from 'react'*/

import { useRef, useState } from 'react'
import { useOutsideClick } from './useOutsideClick'
import Pagina1 from './componentes/Pagina1'
import Pagina2 from './componentes/Pagina2'


function App() {

  const dropDownRef = useRef(null);
  const [isActive, setIsActive] = useState(false)
  const onClick = () => setIsActive(!isActive)

  //links e rota//
  const [pagina,setpagina] = useState(0)

  const retornarPagina=()=>{
    if(pagina==1){
      return <Pagina1/>
    }else if(pagina==2)
    return <Pagina2/>
  }

  return (

   <div className="container">
     <div className="menu-container">

        <button onClick={onClick} className="menu-button">
          <span></span>
          <img width="50px" height="50px" src={logo} alt="menu"></img>
        </button>

        <nav 
          ref={dropDownRef}
          className={`menu ${isActive ? "active" : "inactive"}`}
        >
        <ul>
          <li>
            <button onClick="">cadastrar</button>
          </li>
          <li>
            <button onClick="">entrar</button>

          </li>
          <li>
            <a href="#">Menu</a>
          </li>
         </ul>
         </nav>
      </div>

   <div className='main-principal'>
     <img height="90%" src={logo} alt="logo"></img> <br></br>

     <code>RESTAURANTE EXFOMEADOS</code> <br></br>

    <a id='acesso-cardapio' href="index.html">ACESSAR CARD√ÅPIO</a> <br></br>
   </div>
   {retornarPagina()}
   </div>




  );
 }



export default App;
