import './styles.css';
import { useRef, useState } from 'react'
import Pagina1 from './componentes/Pagina1'
import Pagina2 from './componentes/Pagina2'

export default function App() {

  const dropDownRef = useRef(null);
  const [isActive, setIsActive] = useState(false)
  const onClick = () => setIsActive(!isActive)

    //links e rota//
    const [pagina,setpagina] = useState(0)

    useEffect(
      ()=>{
        const url= window.location.href
        const res= url.split('?')
        setpagina(res[1])
      }
  )

  const LinksPaginas=(p)=>{
    if(p==1) {
     window.open('https://localhost:3000?1','_self')

     } else if(p==2){
      window.open('https://localhost:3000?2','_self')
     } 
    }

  const retornarPagina=()=>{
    if(pagina==1){
      return <Pagina1/>
    }else if(pagina==2){ 
      return <Pagina2/>
    }else{
      return    
              <div>
                 <button onClick=""> cadastrar </button>
                 <button onClick=""> entrar </button>
              </div>
  }
  }


  return (
    <div>
            <button onClick={()=>LinksPaginas(1)}>cadastrar</button>

            <button onClick={()=>LinksPaginas(2)}>entrar</button>
    </div>

  );

}



























